select  misth_final.kodfinal, misth_final.kodxrisi, misth_final.descfinal, misth_final.datefinal, misth_final.title, misth_final.kodkat, misth_zpkat.desckat, misth_final.kodperiod, misth_zpperiod.descperiod, misth_final.aa from misth_final, misth_zpkat, misth_zpperiod WHERE misth_zpkat.kodkat = misth_final.kodkat AND misth_final.kodxrisi = misth_zpkat.kodxrisi AND misth_final.kodperiod = misth_zpperiod.kodperiod AND misth_final.kodxrisi = misth_zpperiod.kodxrisi AND ( ( misth_final.kodxrisi = :arg_kodxrisi )  ) 