select  misth_final.datefinal, misth_final.descfinal, misth_final.kodfinal, misth_final_ypal.kodypal, misth_final_ypal.plirdate, if(isnull(misth_final_ypal.plirdate), 0, 1) as cm_paid from misth_final, misth_final_ypal WHERE misth_final_ypal.kodfinal = misth_final.kodfinal AND misth_final_ypal.kodxrisi = misth_final.kodxrisi AND ( misth_final_ypal.kodypal = :arg_kodypal and misth_final_ypal.kodxrisi = :arg_kodxrisi  ) order by misth_final.datefinal ASC