select  misth_ypal.surname, misth_ypal.name, misth_ypal.fathername, misth_final_ypal.plirdate, misth_final_ypal.kodfinal, misth_final_ypal.kodypal, misth_final_ypal.kodxrisi from misth_final_ypal, misth_ypal WHERE misth_final_ypal.kodypal = misth_ypal.kodypal AND misth_final_ypal.kodxrisi = misth_ypal.kodxrisi AND ( misth_final_ypal.kodfinal = :arg_kodfinal and misth_final_ypal.kodxrisi = :arg_kodxrisi  ) order by misth_ypal.surname ASC, misth_ypal.name ASC, misth_ypal.fathername ASC, misth_ypal.mothername ASC