select  misth_fylo_ypal.kodfylo, misth_fylo_ypal.kodypal, misth_fylo_ypal.kodxrisi, misth_ypal.surname, misth_ypal.name, misth_ypal.fathername from misth_fylo_ypal, misth_ypal WHERE misth_fylo_ypal.kodypal = misth_ypal.kodypal AND misth_fylo_ypal.kodxrisi = misth_ypal.kodxrisi AND ( misth_fylo_ypal.kodfylo = :arg_kodfylo and misth_fylo_ypal.kodxrisi = :arg_kodxrisi  ) order by misth_ypal.surname ASC, misth_ypal.name ASC, misth_ypal.fathername ASC