select  misth_final.descfinal, misth_ypal.surname, misth_ypal.name, misth_ypal.fathername, misth_zpkrat.desckrat, misth_final_ypal_krat.poso, 0 as cm_sel, misth_final_ypal_krat.kodfinal, misth_final_ypal_krat.kodypal, misth_final_ypal_krat.kodkrat, misth_final_ypal_krat.kodxrisi, misth_final_ypal_krat.aa, misth_final_ypal_krat.notes, misth_final_ypal_krat.kodkratapod, misth_final.datefinal, misth_final_ypal.plirdate from misth_final, misth_final_ypal_krat, misth_ypal, misth_final_ypal, misth_zpkrat WHERE misth_final.kodfinal = misth_final_ypal.kodfinal AND misth_final.kodxrisi = misth_final_ypal.kodxrisi AND misth_final_ypal_krat.kodfinal = misth_final_ypal.kodfinal AND misth_final_ypal_krat.kodypal = misth_final_ypal.kodypal AND misth_final_ypal_krat.kodxrisi = misth_final_ypal.kodxrisi AND misth_ypal.kodypal = misth_final_ypal.kodypal AND misth_ypal.kodxrisi = misth_final_ypal.kodxrisi AND misth_zpkrat.kodkrat = misth_final_ypal_krat.kodkrat AND misth_zpkrat.kodxrisi = misth_final_ypal_krat.kodxrisi AND ( misth_final_ypal_krat.kodxrisi = :arg_kodxrisi and misth_final_ypal_krat.kodkratapod is NULL and misth_final_ypal.plirdate is not NULL  ) 