select  usrgroupperm.kodgroup, usrgroupperm.kodaction, usrgroupperm.editrec, usrgroupperm.addrec, usrgroupperm.delrec, usrgroupperm.openlist, usrgroupperm.openform, usrapps.descapp, usractions.descaction from usrgroupperm, usractions, usrapps WHERE usrgroupperm.kodaction = usractions.kodaction AND usractions.kodapp = usrapps.kodapp AND ( usrgroupperm.kodgroup = :arg_kodgroup  ) order by usrapps.descapp ASC, usractions.descaction ASC