select  misth_ypal.surname, misth_ypal.name, misth_ypal.fathername, misth_final_ypal.kodypal from misth_final_ypal, misth_ypal WHERE misth_final_ypal.kodypal = misth_ypal.kodypal AND misth_final_ypal.kodxrisi = misth_ypal.kodxrisi AND ( misth_final_ypal.kodfinal = :arg_kodfinal And misth_final_ypal.kodxrisi = :arg_kodxrisi  ) order by misth_ypal.surname ASC, misth_ypal.name ASC, misth_ypal.fathername ASC