select  misth_final.descfinal, misth_ypal.surname, misth_ypal.name, misth_ypal.fathername, misth_final.kodfinal, misth_final.datefinal, misth_final_ypal.plirdate, misth_final_ypal.kodypal, misth_final_ypal.kodxrisi, misth_zpperiod.descperiod, misth_final.aa from misth_final_ypal, misth_final, misth_ypal, misth_zpperiod WHERE misth_final.kodfinal = misth_final_ypal.kodfinal AND misth_final.kodxrisi = misth_final_ypal.kodxrisi AND misth_ypal.kodypal = misth_final_ypal.kodypal AND misth_ypal.kodxrisi = misth_final_ypal.kodxrisi AND misth_final.kodperiod = misth_zpperiod.kodperiod AND misth_final.kodxrisi = misth_zpperiod.kodxrisi AND ( misth_final.kodxrisi = :arg_kodxrisi and misth_final_ypal.plirdate is null  ) 