select  misth_zpkrat.desckrat, misth_final_ypal_krat.poso, misth_final_ypal_krat.notes from misth_final_ypal_krat, misth_zpkrat WHERE misth_final_ypal_krat.kodkrat = misth_zpkrat.kodkrat AND misth_final_ypal_krat.kodxrisi = misth_zpkrat.kodxrisi AND ( ( ( misth_final_ypal_krat.kodfinal = :arg_kodfinal ) and ( misth_final_ypal_krat.kodypal = :arg_kodypal ) and ( misth_final_ypal_krat.kodxrisi = :arg_kodxrisi ) )  ) order by misth_final_ypal_krat.aa ASC