select  misth_report_ypal.kodreport, misth_report_ypal.kodypal, misth_report_ypal.kodxrisi, misth_ypal.surname, misth_ypal.name, misth_ypal.fathername, misth_report.subtitle, misth_report.prn_notes1, misth_report.prn_notes2 from misth_report_ypal, misth_ypal, misth_report WHERE misth_report_ypal.kodypal = misth_ypal.kodypal AND misth_report_ypal.kodxrisi = misth_ypal.kodxrisi AND misth_report_ypal.kodreport = misth_report.kodreport AND misth_report_ypal.kodxrisi = misth_report.kodxrisi AND ( misth_report_ypal.kodreport = :arg_kodreport and misth_report_ypal.kodxrisi = :arg_kodxrisi  ) order by misth_ypal.surname ASC, misth_ypal.name ASC, misth_ypal.fathername ASC