select  misth_ypal.surname, misth_ypal.name, misth_ypal.fathername, misth_final.aa, misth_final.datefinal, misth_ypal.kodypal, misth_final.title, misth_final.kodfinal from misth_ypal, misth_final_ypal, misth_final WHERE misth_ypal.kodypal = misth_final_ypal.kodypal AND misth_final_ypal.kodxrisi = misth_ypal.kodxrisi AND misth_final.kodfinal = misth_final_ypal.kodfinal AND misth_final.kodxrisi = misth_final_ypal.kodxrisi AND ( misth_final_ypal.kodfinal = :arg_kodfinal and misth_final_ypal.kodxrisi = :arg_kodxrisi  ) order by misth_ypal.surname ASC, misth_ypal.name ASC, misth_ypal.fathername ASC