select  misth_final.datefinal, misth_final.descfinal, misth_final.kodfinal, misth_final_ypal.kodypal, misth_final_ypal.plirdate, misth_zpperiod.descperiod, misth_final.aa from misth_final, misth_final_ypal, misth_zpperiod WHERE misth_final_ypal.kodfinal = misth_final.kodfinal AND misth_final_ypal.kodxrisi = misth_final.kodxrisi AND misth_final.kodperiod = misth_zpperiod.kodperiod AND misth_final.kodxrisi = misth_zpperiod.kodxrisi AND ( misth_final_ypal.kodypal = :arg_kodypal and misth_final_ypal.kodxrisi = :arg_kodxrisi and misth_final_ypal.plirdate is not null  ) order by misth_final.datefinal ASC